// ğŸ¢ TENANT METRICS COLLECTOR
// Coleta mÃ©tricas de todos os tenants para anÃ¡lise de performance

const now = new Date();
const last24h = new Date(now.getTime() - 24 * 60 * 60 * 1000);
const last7d = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
const last30d = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);

// Lista de tenants para coletar mÃ©tricas (em produÃ§Ã£o viria da tabela tenants)
const tenants = [
  {
    tenant_id: 'tenant_1_beleza',
    name: 'SalÃ£o ElegÃ¢ncia Premium',
    domain: 'beauty'
  },
  {
    tenant_id: 'tenant_2_saude',
    name: 'ClÃ­nica Vida SaudÃ¡vel', 
    domain: 'healthcare'
  },
  {
    tenant_id: 'tenant_3_juridico',
    name: 'Advocacia Silva & Santos',
    domain: 'legal'
  },
  {
    tenant_id: 'tenant_4_educacao',
    name: 'EduTech Cursos Personalizados',
    domain: 'education'
  },
  {
    tenant_id: 'tenant_5_esportes',
    name: 'FitPro Academia & Personal',
    domain: 'sports'
  },
  {
    tenant_id: 'tenant_6_consultoria',
    name: 'BizConsult EstratÃ©gia Empresarial',
    domain: 'consulting'
  }
];

// Gerar batch ID para esta coleta
const batchId = `metrics_${now.toISOString().split('T')[0]}_${now.getHours()}_${Math.random().toString(36).substr(2, 6)}`;

// Preparar dados para coleta individual por tenant
const metricsCollection = tenants.map(tenant => ({
  tenant_id: tenant.tenant_id,
  tenant_name: tenant.name,
  business_domain: tenant.domain,
  collection_batch_id: batchId,
  collection_started_at: now.toISOString(),
  period_24h_start: last24h.toISOString(),
  period_7d_start: last7d.toISOString(),
  period_30d_start: last30d.toISOString(),
  current_timestamp: now.toISOString()
}));

return metricsCollection.map(item => ({ json: item }));

// ğŸ“Š METRICS CALCULATOR
// Calcula mÃ©tricas de performance para cada tenant

const tenantData = $('ğŸ¢ Tenant Collector').item.json;
const appointments = $('ğŸ“… Fetch Appointments').all().map(item => item.json);
const conversations = $('ğŸ’¬ Fetch Conversations').all().map(item => item.json);
const aiCostRecords = $('ğŸ’° Fetch AI Costs').all().map(item => item.json);

const now = new Date();
const last24h = new Date(tenantData.period_24h_start);
const last7d = new Date(tenantData.period_7d_start);
const last30d = new Date(tenantData.period_30d_start);

// FunÃ§Ã£o auxiliar para filtrar por perÃ­odo
function filterByPeriod(items, dateField, periodStart) {
  return items.filter(item => {
    const itemDate = new Date(item[dateField]);
    return itemDate >= periodStart;
  });
}

// MÃ©tricas de agendamentos por perÃ­odo
const appointments24h = filterByPeriod(appointments, 'created_at', last24h);
const appointments7d = filterByPeriod(appointments, 'created_at', last7d);
const appointments30d = filterByPeriod(appointments, 'created_at', last30d);

// MÃ©tricas de conversas por perÃ­odo
const conversations24h = filterByPeriod(conversations, 'created_at', last24h);
const conversations7d = filterByPeriod(conversations, 'created_at', last7d);
const conversations30d = filterByPeriod(conversations, 'created_at', last30d);

// MÃ©tricas de custos IA por perÃ­odo (da conversation_history)
const aiCosts24h = filterByPeriod(aiCostRecords, 'created_at', last24h);
const aiCosts7d = filterByPeriod(aiCostRecords, 'created_at', last7d);
const aiCosts30d = filterByPeriod(aiCostRecords, 'created_at', last30d);

// Calcular receita por perÃ­odo
function calculateRevenue(appointmentsList) {
  return appointmentsList
    .filter(apt => ['confirmed', 'completed'].includes(apt.status))
    .reduce((sum, apt) => sum + (apt.final_price || apt.quoted_price || 0), 0);
}

const revenue24h = calculateRevenue(appointments24h);
const revenue7d = calculateRevenue(appointments7d);
const revenue30d = calculateRevenue(appointments30d);

// Calcular custos de IA da conversation_history (api_cost_usd + processing_cost_usd)
function calculateAICosts(aiRecordsList) {
  return aiRecordsList.reduce((sum, record) => {
    const apiCost = parseFloat(record.api_cost_usd || 0);
    const processingCost = parseFloat(record.processing_cost_usd || 0);
    return sum + apiCost + processingCost;
  }, 0);
}

const aiCostsTotal24h = calculateAICosts(aiCosts24h);
const aiCostsTotal7d = calculateAICosts(aiCosts7d);
const aiCostsTotal30d = calculateAICosts(aiCosts30d);

// MÃ©tricas de conversÃ£o
const conversionRate24h = conversations24h.length > 0 ? 
  (appointments24h.length / conversations24h.length * 100) : 0;
const conversionRate7d = conversations7d.length > 0 ? 
  (appointments7d.length / conversations7d.length * 100) : 0;
const conversionRate30d = conversations30d.length > 0 ? 
  (appointments30d.length / conversations30d.length * 100) : 0;

// AnÃ¡lise de status dos agendamentos
function analyzeAppointmentStatus(appointmentsList) {
  const statusCount = {};
  appointmentsList.forEach(apt => {
    const status = apt.status || 'unknown';
    statusCount[status] = (statusCount[status] || 0) + 1;
  });
  return statusCount;
}

const statusAnalysis24h = analyzeAppointmentStatus(appointments24h);
const statusAnalysis7d = analyzeAppointmentStatus(appointments7d);
const statusAnalysis30d = analyzeAppointmentStatus(appointments30d);

// MÃ©tricas de engajamento
const avgMessagesPerConversation = conversations30d.length > 0 ?
  conversations30d.length / new Set(conversations30d.map(c => c.user_id)).size : 0;

// AnÃ¡lise de intents
function analyzeIntents(conversationsList) {
  const intentCount = {};
  conversationsList.forEach(conv => {
    const intent = conv.intent_detected || 'unknown';
    intentCount[intent] = (intentCount[intent] || 0) + 1;
  });
  return intentCount;
}

const intentAnalysis30d = analyzeIntents(conversations30d);

// ROI (Return on Investment)
const roi24h = aiCostsTotal24h > 0 ? ((revenue24h - aiCostsTotal24h) / aiCostsTotal24h * 100) : 0;
const roi7d = aiCostsTotal7d > 0 ? ((revenue7d - aiCostsTotal7d) / aiCostsTotal7d * 100) : 0;
const roi30d = aiCostsTotal30d > 0 ? ((revenue30d - aiCostsTotal30d) / aiCostsTotal30d * 100) : 0;

// Preparar dados para armazenar na tabela tenant_metrics REAL
const tenantMetricsData = {
  tenant_id: tenantData.tenant_id,
  metric_type: 'comprehensive',
  period: '30d', // PerÃ­odo principal
  metric_data: {
    // Dados bÃ¡sicos do tenant
    tenant_name: tenantData.tenant_name,
    business_domain: tenantData.business_domain,
    collection_batch_id: tenantData.collection_batch_id,

    // MÃ©tricas de volume - 24h
    appointments_24h: appointments24h.length,
    conversations_24h: conversations24h.length,
    revenue_24h: revenue24h,
    ai_costs_24h: aiCostsTotal24h,
    conversion_rate_24h: parseFloat(conversionRate24h.toFixed(2)),
    roi_24h: parseFloat(roi24h.toFixed(2)),

    // MÃ©tricas de volume - 7d
    appointments_7d: appointments7d.length,
    conversations_7d: conversations7d.length,
    revenue_7d: revenue7d,
    ai_costs_7d: aiCostsTotal7d,
    conversion_rate_7d: parseFloat(conversionRate7d.toFixed(2)),
    roi_7d: parseFloat(roi7d.toFixed(2)),

    // MÃ©tricas de volume - 30d
    appointments_30d: appointments30d.length,
    conversations_30d: conversations30d.length,
    revenue_30d: revenue30d,
    ai_costs_30d: aiCostsTotal30d,
    conversion_rate_30d: parseFloat(conversionRate30d.toFixed(2)),
    roi_30d: parseFloat(roi30d.toFixed(2)),

    // AnÃ¡lises detalhadas
    appointment_status_24h: statusAnalysis24h,
    appointment_status_7d: statusAnalysis7d,
    appointment_status_30d: statusAnalysis30d,
    intent_distribution: intentAnalysis30d,
    avg_messages_per_conversation: parseFloat(avgMessagesPerConversation.toFixed(2)),

    // MÃ©tricas derivadas
    revenue_per_conversation_30d: conversations30d.length > 0 ? parseFloat((revenue30d / conversations30d.length).toFixed(2)) : 0,
    cost_per_conversation_30d: conversations30d.length > 0 ? parseFloat((aiCostsTotal30d / conversations30d.length).toFixed(4)) : 0,
    profit_margin_30d: revenue30d > 0 ? parseFloat(((revenue30d - aiCostsTotal30d) / revenue30d * 100).toFixed(2)) : 0,

    // KPIs de saÃºde do negÃ³cio
    active_users_30d: new Set(conversations30d.map(c => c.user_id)).size,
    repeat_customers_30d: new Set(appointments30d.filter(apt => 
      appointments30d.filter(a => a.user_id === apt.user_id).length > 1
    ).map(apt => apt.user_id)).size
  },
  calculated_at: new Date().toISOString()
};

return [tenantMetricsData];

// ğŸ“ˆ PLATFORM AGGREGATOR
// Agrega mÃ©tricas de todos os tenants para dashboard super admin

const allTenantMetrics = $input.all();

// Calcular totais da plataforma
const platformTotals = allTenantMetrics.reduce((totals, tenant) => {
  const tenantMetrics = tenant.json.metric_data;

  return {
    // Totais de volume
    total_appointments_24h: (totals.total_appointments_24h || 0) + (tenantMetrics.appointments_24h || 0),
    total_appointments_7d: (totals.total_appointments_7d || 0) + (tenantMetrics.appointments_7d || 0),
    total_appointments_30d: (totals.total_appointments_30d || 0) + (tenantMetrics.appointments_30d || 0),

    total_conversations_24h: (totals.total_conversations_24h || 0) + (tenantMetrics.conversations_24h || 0),
    total_conversations_7d: (totals.total_conversations_7d || 0) + (tenantMetrics.conversations_7d || 0),
    total_conversations_30d: (totals.total_conversations_30d || 0) + (tenantMetrics.conversations_30d || 0),

    // Totais financeiros
    total_revenue_24h: (totals.total_revenue_24h || 0) + (tenantMetrics.revenue_24h || 0),
    total_revenue_7d: (totals.total_revenue_7d || 0) + (tenantMetrics.revenue_7d || 0),
    total_revenue_30d: (totals.total_revenue_30d || 0) + (tenantMetrics.revenue_30d || 0),

    total_ai_costs_24h: (totals.total_ai_costs_24h || 0) + (tenantMetrics.ai_costs_24h || 0),
    total_ai_costs_7d: (totals.total_ai_costs_7d || 0) + (tenantMetrics.ai_costs_7d || 0),
    total_ai_costs_30d: (totals.total_ai_costs_30d || 0) + (tenantMetrics.ai_costs_30d || 0),

    // Totais de usuÃ¡rios
    total_active_users_30d: (totals.total_active_users_30d || 0) + (tenantMetrics.active_users_30d || 0),
    total_repeat_customers_30d: (totals.total_repeat_customers_30d || 0) + (tenantMetrics.repeat_customers_30d || 0),

    // Contadores
    total_active_tenants: (totals.total_active_tenants || 0) + 1
  };
}, {});

// Calcular mÃ©dias da plataforma
const tenantCount = allTenantMetrics.length;
const avgConversionRate30d = tenantCount > 0 ? 
  allTenantMetrics.reduce((sum, t) => sum + (t.json.metric_data.conversion_rate_30d || 0), 0) / tenantCount : 0;
const avgROI30d = tenantCount > 0 ? 
  allTenantMetrics.reduce((sum, t) => sum + (t.json.metric_data.roi_30d || 0), 0) / tenantCount : 0;

// Calcular mÃ©tricas derivadas da plataforma
const platformConversionRate30d = platformTotals.total_conversations_30d > 0 ?
  (platformTotals.total_appointments_30d / platformTotals.total_conversations_30d * 100) : 0;
const platformROI30d = platformTotals.total_ai_costs_30d > 0 ?
  ((platformTotals.total_revenue_30d - platformTotals.total_ai_costs_30d) / platformTotals.total_ai_costs_30d * 100) : 0;

// AnÃ¡lise por domÃ­nio de negÃ³cio
const domainAnalysis = allTenantMetrics.reduce((domains, tenant) => {
  const domain = tenant.json.metric_data.business_domain;
  const metrics = tenant.json.metric_data;

  if (!domains[domain]) {
    domains[domain] = {
      tenant_count: 0,
      total_revenue_30d: 0,
      total_appointments_30d: 0,
      total_conversations_30d: 0,
      avg_conversion_rate_30d: 0
    };
  }

  domains[domain].tenant_count++;
  domains[domain].total_revenue_30d += metrics.revenue_30d || 0;
  domains[domain].total_appointments_30d += metrics.appointments_30d || 0;
  domains[domain].total_conversations_30d += metrics.conversations_30d || 0;
  domains[domain].avg_conversion_rate_30d += metrics.conversion_rate_30d || 0;

  return domains;
}, {});

// Calcular mÃ©dias por domÃ­nio
Object.keys(domainAnalysis).forEach(domain => {
  domainAnalysis[domain].avg_conversion_rate_30d /= domainAnalysis[domain].tenant_count;
  domainAnalysis[domain].avg_conversion_rate_30d = parseFloat(domainAnalysis[domain].avg_conversion_rate_30d.toFixed(2));
});

// Identificar top performers
const topPerformers = {
  highest_revenue: allTenantMetrics.reduce((max, current) => 
    (current.json.metric_data.revenue_30d || 0) > (max.json?.metric_data?.revenue_30d || 0) ? current : max, { json: { metric_data: {} } }),
  highest_conversion: allTenantMetrics.reduce((max, current) => 
    (current.json.metric_data.conversion_rate_30d || 0) > (max.json?.metric_data?.conversion_rate_30d || 0) ? current : max, { json: { metric_data: {} } }),
  highest_roi: allTenantMetrics.reduce((max, current) => 
    (current.json.metric_data.roi_30d || 0) > (max.json?.metric_data?.roi_30d || 0) ? current : max, { json: { metric_data: {} } }),
  most_active: allTenantMetrics.reduce((max, current) => 
    (current.json.metric_data.conversations_30d || 0) > (max.json?.metric_data?.conversations_30d || 0) ? current : max, { json: { metric_data: {} } })
};

// Preparar dados para armazenar na tabela platform_metrics REAL
const platformMetricsData = {
  platform_id: 'PLATFORM',
  period: '30d',
  metric_type: 'comprehensive',
  metric_data: {
    // Dados bÃ¡sicos
    collection_batch_id: allTenantMetrics[0]?.json.metric_data.collection_batch_id,
    aggregation_timestamp: new Date().toISOString(),
    total_tenants_processed: tenantCount,

    // MÃ©tricas agregadas
    ...platformTotals,

    // Conversion rates da plataforma
    platform_conversion_rate_30d: parseFloat(platformConversionRate30d.toFixed(2)),
    platform_roi_30d: parseFloat(platformROI30d.toFixed(2)),

    // MÃ©dias entre tenants
    avg_tenant_conversion_30d: parseFloat(avgConversionRate30d.toFixed(2)),
    avg_tenant_roi_30d: parseFloat(avgROI30d.toFixed(2)),

    // AnÃ¡lises especiais
    domain_analysis: domainAnalysis,
    top_performers: {
      highest_revenue_tenant: topPerformers.highest_revenue.json.metric_data.tenant_name,
      highest_revenue_amount: topPerformers.highest_revenue.json.metric_data.revenue_30d,
      highest_conversion_tenant: topPerformers.highest_conversion.json.metric_data.tenant_name,
      highest_conversion_rate: topPerformers.highest_conversion.json.metric_data.conversion_rate_30d,
      highest_roi_tenant: topPerformers.highest_roi.json.metric_data.tenant_name,
      highest_roi_percentage: topPerformers.highest_roi.json.metric_data.roi_30d,
      most_active_tenant: topPerformers.most_active.json.metric_data.tenant_name,
      most_active_conversations: topPerformers.most_active.json.metric_data.conversations_30d
    }
  }
};

return [platformMetricsData];

// ğŸ“Š REAL-TIME ANALYTICS DASHBOARD
// Gera snapshot em tempo real para dashboards

const requestData = $json;
const requestedMetrics = requestData.metrics || ['all'];
const tenantFilter = requestData.tenant_id || null;
const domainFilter = requestData.domain || null;
const periodFilter = requestData.period || '30d'; // 24h, 7d, 30d

// Timestamp da solicitaÃ§Ã£o
const requestTimestamp = new Date().toISOString();

// Determinar perÃ­odo de filtro
const periodHours = {
  '24h': 24,
  '7d': 7 * 24,
  '30d': 30 * 24
};

const hoursBack = periodHours[periodFilter] || 720; // default 30d
const periodStart = new Date(Date.now() - hoursBack * 60 * 60 * 1000).toISOString();

return [{
  analytics_request: {
    requested_metrics: requestedMetrics,
    tenant_filter: tenantFilter,
    domain_filter: domainFilter,
    period_filter: periodFilter,
    period_start: periodStart,
    request_timestamp: requestTimestamp,
    request_id: `analytics_${Date.now()}_${Math.random().toString(36).substr(2, 6)}`
  }
}];

// âœ… ANALYTICS SUCCESS RESPONSE
// Resposta de sucesso da coleta de analytics

const analyticsData = $input.all();
const isBatchProcessing = analyticsData.some(item => item.json.metric_data?.collection_batch_id);
const isManualRequest = analyticsData.some(item => item.json.analytics_request);

if (isBatchProcessing) {
  // Resposta da coleta automÃ¡tica
  const platformData = analyticsData.find(item => item.json.platform_id);
  const tenantMetrics = analyticsData.filter(item => item.json.tenant_id && !item.json.platform_id);

  return [{
    status: 'analytics_collection_completed',
    message: 'Business analytics collected successfully',
    data: {
      batch_id: platformData?.json.metric_data?.collection_batch_id,
      tenants_processed: tenantMetrics.length,
      platform_revenue_30d: platformData?.json.metric_data?.total_revenue_30d || 0,
      platform_appointments_30d: platformData?.json.metric_data?.total_appointments_30d || 0,
      platform_conversion_rate_30d: platformData?.json.metric_data?.platform_conversion_rate_30d || 0,
      platform_roi_30d: platformData?.json.metric_data?.platform_roi_30d || 0,
      top_revenue_tenant: platformData?.json.metric_data?.top_performers?.highest_revenue_tenant,
      processing_completed_at: new Date().toISOString()
    },
    timestamp: new Date().toISOString()
  }];
} else if (isManualRequest) {
  // Resposta da solicitaÃ§Ã£o manual
  const requestData = analyticsData.find(item => item.json.analytics_request);

  return [{
    status: 'manual_analytics_processed',
    message: 'Manual analytics request processed',
    data: {
      request_id: requestData?.json.analytics_request?.request_id,
      metrics_provided: requestData?.json.analytics_request?.requested_metrics,
      period_analyzed: requestData?.json.analytics_request?.period_filter,
      processing_completed_at: new Date().toISOString()
    },
    timestamp: new Date().toISOString()
  }];
} else {
  // Resposta genÃ©rica
  return [{
    status: 'analytics_processed',
    message: 'Analytics processing completed',
    data: {
      items_processed: analyticsData.length,
      processing_completed_at: new Date().toISOString()
    },
    timestamp: new Date().toISOString()
  }];
}
